INSERT INTO `wp_postmeta` VALUES("5898", "858", "email_template", "<!-- Start of seperator -->\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" bgcolor=\"#F0F0F0\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table class=\"devicewidth2\" border=\"0\" width=\"600\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-size: 1px; line-height: 1px;\" align=\"center\" height=\"14\"></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<!-- End of seperator -->\r\n<!-- Start logo -->\r\n\r\n<!-- Start header image -->\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" bgcolor=\"#F0F0F0\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table class=\"devicewidth2\" border=\"0\" width=\"600\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td width=\"100%\">\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<!-- End header image -->\r\n<!-- start main content -->\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" bgcolor=\"#F0F0F0\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table class=\"devicewidth2\" border=\"0\" width=\"600\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td width=\"100%\">\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"12\">\r\n<tbody>\r\n<tr bgcolor=\"#0069AA\">\r\n<td style=\"font: bold 22px/26px Trebuchet MS; color: #fff;\">CloudBand Ecosystem Newsletter\r\n\r\n<span style=\"font: 18px/22px Trebuchet MS; color: #fff;\">Get the Latest News from CloudBand Ecosystem Community\r\n\r\nEdition 19 - October 2015</span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table class=\"devicewidth2\" border=\"0\" width=\"600\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\" bgcolor=\"#FFFFFF\"><!-- Spacing -->\r\n<tbody>\r\n<tr>\r\n<td style=\"font-size: 1px; line-height: 1px; mso-line-height-rule: exactly;\" height=\"20\"></td>\r\n</tr>\r\n<!-- Spacing -->\r\n<tr>\r\n<td>\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td id=\"Email-Content\" class=\"mktEditable\" style=\"font: 14px/18px Trebuchet MS; color: #333;\" align=\"left\">\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td style=\"padding-left: 20px; padding-right: 20px; font: 14px/18px Trebuchet MS; color: #333;\">Dear Ecosystem Members and Friends,\r\n\r\n\r\n\r\nThis month, the CloudBand Ecosystem expands with 4 new members, Symantec, Mobigen, Nexus Telecom and Iskratel. They will help us enhance our community knowledge in the fields of security, OSS, DPI and Big Data.\r\n\r\n\r\n\r\nComing up in October is the SDN &amp; Openflow World Congress in Dusseldorf, Germany. We are a Diamond sponsor as well as one of the official Ecosystem sponsors. Take a look at the rich agenda we are preparing for this event starting with a workshop dedicated to the Ecosystem work on Monday 12th. We hope to see many of you there!\r\n\r\n\r\n\r\nValerie</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td class=\"mktEditable\" style=\"font: 14px/18px Trebuchet MS; color: #333;\" align=\"left\">\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr bgcolor=\"#0069AA\">\r\n<td width=\"15\" height=\"25\"></td>\r\n<td style=\"font: 14px/18px Trebuchet MS; color: #fff;\" align=\"left\"><strong>EVENT COVERAGE</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding-left: 20px; padding-right: 20px; font: 14px/18px Trebuchet MS; color: #333;\"><a href=\"http://www.layer123.com/sdn\"><strong>SDN and OpenFlow World Congress</strong></a>, October 12-14, Düsseldorf- Germany.\r\n\r\nAs a Diamond and ecosystem sponsor of this conference, Alcatel-Lucent will have a strong presence with key executives, top level speakers and panelists, a private workshop and live SDN &amp; NFV demonstrations. Through our Ecosystem sponsorship, we are able to offer a 20% discount <strong>exclusively</strong> to CloudBand ecosystem partners. Please register on <a href=\"http://infoview.all.alcatel-lucent.com/cgi-bin/goto?goto=http://www.layer123.com/sdn-booknow&amp;mail=CLOUDBANDSALES&amp;date=20150910&amp;issue=CLOUDBANDSALES.2015.253.html\">www.layer123.com/sdn-booknow</a> and enter the code <strong>CLDB2015</strong> for the discounted passes.\r\n\r\n\r\n\r\nAlcatel-Lucent will be running several workshops before and during the conference:\r\n<ul>\r\n	<li>Monday 12th 13:30-15:10: An ecosystem approach to demonstrating VNF interoperability</li>\r\n	<li>Monday 12th 15:30-17:30: SDN &amp; NFV – new services, news ways without compromise</li>\r\n	<li><strong>Tuesday 13th 18:00-19:00: Ecosystem Members private session.</strong> Registration required, please <a href=\"mailto:valerie.noto@alcatel-lucent.com\">email us</a>.</li>\r\n</ul>\r\nSeven speakers from Alcatel-Lucent will take the stage at the conference, including in particular:\r\n<ul>\r\n	<li>Tuesday 13 October, 09:50 -10:15: Ron Haberman, VP &amp; GM of CloudBand Business Unit – “on the path to openness”</li>\r\n	<li>Tuesday 13 October, 14:45 – 15:10: Sunil Khandekar, Founder &amp; CEO, Nuage Networks</li>\r\n	<li>Tuesday 13 October 17.35 – 18.00: Panel debate – Building the Ecosystem for NFV, Impact of Open Source – Valerie Noto, Director, CloudBand Ecosystem</li>\r\n	<li>Tuesday 13 October 17.35 – 18.00: Panel debate – Speeding the adoption rate of NFV, how to straighten the path – Eliaz Tobias, Sr Director Product Management, CloudBand</li>\r\n</ul>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td class=\"mktEditable\" style=\"font: 14px/18px Trebuchet MS; color: #333;\" align=\"left\">\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr bgcolor=\"#0069AA\">\r\n<td width=\"15\" height=\"25\"></td>\r\n<td style=\"font: 14px/18px Trebuchet MS; color: #fff;\" align=\"left\"><strong>NEWS</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding-left: 20px; padding-right: 20px; font: 14px/18px Trebuchet MS; color: #333;\"><a href=\"http://appledoreresearch.com/wp-content/uploads/2015/05/MANO1.png\"><strong>Appledore Scorecard - Alcatel-Lucent a leading supplier in the NFV MANO marketplace</strong></a>\r\n\r\nAppledore Research Group recently completed research on leading suppliers in the market of Management and Orchestration solutions. Appledore evaluated 12 suppliers against a set of 10 criteria for operational support of NFV, SDN, and Cloud services. It also evaluated each supplier on their execution in the market – a combined assessment of trials, live deployments, and the business impact of NFV and SDN deployments. The <a href=\"http://appledoreresearch.com/wp-content/uploads/2015/05/MANO1.png\">results</a> clearly position Alcatel-Lucent as the leader in this area.</td>\r\n</tr>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td class=\"mktEditable\" style=\"font: 14px/18px Trebuchet MS; color: #333;\" align=\"left\">\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr bgcolor=\"#0069AA\">\r\n<td width=\"15\" height=\"25\"></td>\r\n<td style=\"font: 14px/18px Trebuchet MS; color: #fff;\" align=\"left\"><strong>WHITEPAPERS</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding-left: 20px; padding-right: 20px; font: 14px/18px Trebuchet MS; color: #333;\"><a href=\"http://www2.alcatel-lucent.com/landing/mini-series/\"><strong>Bridging the Gap between legacy and Cloud, NFV Miniseries:</strong></a>\r\n\r\nBy now a lot of the network functions virtualization (NFV) promises have been shown to be technically feasible. But can NFV provide the same reliability, availability and manageability as traditional network solutions. What if something goes wrong? What if there is a failure? Who is to blame? Learn the answers to these questions - and more - in our white paper mini-series: <a href=\"http://www2.alcatel-lucent.com/landing/mini-series/#sthash.RzhzglJ0.dpuf\">Bridging the gaps between legacy functions and the cloud</a>. Three papers have been published so far: Root cause analysis in NFV, Monitoring challenges and High availability.</td>\r\n</tr>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td class=\"mktEditable\" style=\"font: 14px/18px Trebuchet MS; color: #333;\" align=\"left\">\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr bgcolor=\"#0069AA\">\r\n<td width=\"15\" height=\"25\"></td>\r\n<td style=\"font: 14px/18px Trebuchet MS; color: #fff;\" align=\"left\"><strong>INTERVIEWS</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding-left: 20px; padding-right: 20px; font: 14px/18px Trebuchet MS; color: #333;\"><a href=\"http://www.telecomtv.com/articles/alcatel-lucent/nfv-certified-and-ready-to-go-12812/\"><strong>Moving NFV into commercial networks : the network functions vendors perspective</strong></a>\r\n\r\nThe adoption of Virtualization by the Network Functions supplier is a determining success factor for NFV. Four suppliers of network applications recently shared with TelecomTV their view on the progress of virtualization and the opportunities and challenges of deploying NFV based solution in commercial networks. You can watch a short highlight of interviews with F5, Radware, Procera and JDSU <a href=\"http://www.telecomtv.com/articles/alcatel-lucent/nfv-certified-and-ready-to-go-12812/\">here</a>.\r\n\r\n\r\n\r\n<a href=\"https://redskyvision.wistia.com/medias/atkvcok8e4\"><strong>Enabling the industry shift from hardware towards software</strong></a>\r\n\r\nIn this <a href=\"https://redskyvision.wistia.com/medias/atkvcok8e4\">short interview</a>, Idan Mor, Sr Director CloudBand Alliances looks back at the genesis of CloudBand, an initiative that started out from the motivation of Alcatel-Lucent for moving its own software from dedicated hardware-based solution towards more flexible cloud based solutions and that is today enabling the larger industry in its “Cloudshift” transformation journey.</td>\r\n</tr>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td class=\"mktEditable\" style=\"font: 14px/18px Trebuchet MS; color: #333;\" align=\"left\">\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr bgcolor=\"#0069AA\">\r\n<td width=\"15\" height=\"25\"></td>\r\n<td style=\"font: 14px/18px Trebuchet MS; color: #fff;\" align=\"left\"><strong>BLOG</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding-left: 20px; padding-right: 20px; font: 14px/18px Trebuchet MS; color: #333;\"><a href=\"http://www.lightreading.com/nfv/nfv-mano/bringing-together-nfv-mano-and-oss/a/d-id/716318?_mc=RSS_LR_EDT\"><strong>LightReading - Bringing Together NFV MANO &amp; OSS :</strong></a>\r\n\r\nIn this 3rd post of her Blog Series on NFV, Caroline Chappell looks at the complementary role of NFV Management and Network Orchestration with the Operation and Support System and the need for the OSS to evolve to accommodate the highly dynamic environment created by NFV. She presents a vision for a realistic gradual evolution of OSS that could hold back the transformation to NFV. Continue reading Caroline´s <a href=\"http://www.lightreading.com/nfv/nfv-mano/bringing-together-nfv-mano-and-oss/a/d-id/716318?_mc=RSS_LR_EDT\">blog</a>, and access the previous two parts <a href=\"http://www.lightreading.com/nfv/nfv-specs-open-source/openstack-doesnt-come-for-free/a/d-id/715626\">here</a> and <a href=\"http://www.lightreading.com/nfv/nfv-specs-open-source/migrating-to-nfv-a-step-at-a-time/a/d-id/715769\">here</a>.\r\n\r\n\r\n\r\n<a href=\"http://blog.cimicorp.com/?p=2251\"><strong>Tom Nolle - Could ALU’s Recent “NFV Wins” Show a Shift in Focus for the Industry? :</strong></a>\r\n\r\nIn this blog post, Tom Nolle, President of CIMI Corp., shares his thought about what the recent NFV wins of Alcatel-Lucent in China and with Telefonica reveal about how the industry is embracing NFV. Tom argues that these successes validate the vision of Cloud-centric future, with operations efficiency and service agility as the prime benefits of NFV. Read more <a href=\"http://blog.cimicorp.com/?p=2251\">here</a>.\r\n\r\n<strong><a href=\"http://ecosystem.cloud-band.com/transformation-for-nfv-fifth-lesson-professional-services-in-nfv-where-do-they-fit-in/\">\r\n\r\nTransformation for NFV - fifth lesson: professional services in NFV – where do they fit ?</a></strong>\r\n\r\nA central promise of NFV is to optimize operational expenditure for telecom projects. This necessarily entails a has a huge impact on professional services, which traditionally have occupied a significant, and sometimes uncontrollable part of these projects. But rather than spelling the end of professional services, the transformation for NFV is an opportunity for vendors, integrators and service provider to focus on high added-value (design, architecture etc…) and strategic activities rather than on low end (installation, deployment …) activities. Guy examines more in detail this transformation and the opportunities that it offers in his latest <a href=\"http://ecosystem.cloud-band.com/transformation-for-nfv-fifth-lesson-professional-services-in-nfv-where-do-they-fit-in/\">blog post</a>.\r\n\r\n\r\n\r\n<a href=\"https://www.alcatel-lucent.com/blog/2015/stagefright-highlights-need-more-security\"><strong>The need for holistic approach to mobile device security:</strong></a>\r\n\r\nThe latest Alcatel-Lucent <a href=\"https://www.alcatel-lucent.com/solutions/malware-reports\">security</a> report shows the trend of mobile malware infections increasing in number, sophistication and, most importantly, severity. At the same time, this trend represents an opportunity for service providers to provide a network based security solutions, such as Alcatel-Lucent <a href=\"https://www.alcatel-lucent.com/products/motive-security-guardian\">Motive® Security Guardian</a> which can leverage a cloud-based deployment architecture to rapidly deploy at a network-wide level responses to new security threats.</td>\r\n</tr>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td class=\"mktEditable\" style=\"font: 14px/18px Trebuchet MS; color: #333;\" align=\"left\">\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr bgcolor=\"#0069AA\">\r\n<td width=\"15\" height=\"25\"></td>\r\n<td style=\"font: 14px/18px Trebuchet MS; color: #fff;\" align=\"left\"><strong>WEBINAR</strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr>\r\n<td style=\"padding-left: 20px; padding-right: 20px; font: 14px/18px Trebuchet MS; color: #333;\"><a href=\"https://www.brighttalk.com/webcast/6985/172207\"><strong>APPLYING LEAN OPERATIONAL PRINCIPLES TO NFV, September 29th 17:00 CET, 11:00 EST</strong></a>\r\n\r\nIn this SDN &amp; Openflow World Congress pre-event webinar, Alcatel-Lucent will highlight how Lean principles are being applied to the operationalization of NFV to enable efficient and automated dynamic services. The session will outline a framework for network and service orchestration followed by a demonstration of Lean NFV operations launching VPN and VoLTE services using an OpenStack based platform. Register for this webinar <a href=\"https://www.brighttalk.com/webcast/6985/172207\">here</a>.\r\n\r\n\r\n\r\n<a href=\"http://event.on24.com/wcc/r/1030160/0164F54BB660B574DB2A978979D395A6\" target=\"_blank\"><strong>NFV Mashup Series #19: Prepare your network for NFV – View on Demand</strong></a>\r\n\r\nTo help service providers develop a comprehensive evaluation of NFV benefits, Bell Labs Consulting has developed a number of techno-economic models over the last few years that quantify its impact on service providers’ overall business. During this period, we have seen value proposition emphasis shift from CapEx reduction to OpEx savings, and then on to superior customer experience and new revenue creation. In this mashup, Bell Labs Consulting presents a comprehensive view of NFV economics and shares key insights for making superior strategic decisions pertaining to NFV. Access this on demand webinar <a href=\"http://event.on24.com/wcc/r/1030160/0164F54BB660B574DB2A978979D395A6\" target=\"_blank\">here</a>.</td>\r\n</tr>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<!-- TEST -->\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td class=\"mktEditable\" style=\"font: 14px/18px Trebuchet MS; color: #333;\" align=\"left\">\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td></td>\r\n</tr>\r\n<tr bgcolor=\"#0069AA\">\r\n<td style=\"padding: 20px; font: 14px/18px Trebuchet MS; color: #fff; text-align: center;\"><a style=\"font-weight: bold; color: #fff; text-decoration: underline;\" href=\"https://pages.alcatel-lucent.com/CloudBand.html\" target=\"_blank\">Subscribe now</a> or <a style=\"color: #fff; text-decoration: underline; font-weight: bold;\" href=\"{{system.forwardToFriendLink}}\" target=\"_blank\">Forward to a friend</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<!-- END TEST --></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<!-- End main content -->\r\n<!-- Start of seperator -->\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" bgcolor=\"#F0F0F0\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table class=\"devicewidth2\" border=\"0\" width=\"600\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-size: 1px; line-height: 1px;\" align=\"center\" height=\"20\"></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<!-- End of seperator -->\r\n<!-- start of footer -->\r\n<table border=\"0\" width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" bgcolor=\"#F0F0F0\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table class=\"devicewidth2\" border=\"0\" width=\"600\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table class=\"devicewidth2\" border=\"0\" width=\"600\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\" bgcolor=\"#F0F0F0\">\r\n<tbody>\r\n