INSERT INTO `wp_posts` VALUES("64", "1", "2015-07-07 10:32:50", "2015-07-07 10:32:50", "Telefónica is one of the largest telecommunications companies in the world in terms of market capitalisation and number of customers. From this outstanding position in the industry, and with its mobile, fixed and broadband businesses as the key drivers of its growth, Telefónica has focused its strategy on becoming a leading company in the digital world.\r\nThe company has a significant presence in 24 countries and a customer base that amounts more than 320 million accesses around the world.", "Telefonica", "", "publish", "closed", "closed", "", "telefonica", "", "", "2015-07-22 15:39:12", "2015-07-22 15:39:12", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=64", "0", "board_members", "", "0");
INSERT INTO `wp_posts` VALUES("142", "1", "2015-07-22 15:44:28", "2015-07-22 15:44:28", "Alcatel-Lucent’s vIMS is a cloud communications platform that brings our complete IMS portfolio into the cloud. This fully virtualized, software-only solution can be deployed on COTS hardware and operates in an open, automated , dynamic and scalable cloud environment . It unites the best cloud technologies with high performance virtualized software to help you get to VoLTE faster and reach new profitable web and Enterprise markets.", "Alcatel-Lucent’s vIMS", "", "publish", "closed", "closed", "", "alcatel-lucents-vims", "", "", "2015-07-22 15:44:28", "2015-07-22 15:44:28", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=142", "0", "board_members", "", "0");
