INSERT INTO `wp_posts` VALUES("149", "1", "2015-07-22 15:47:33", "2015-07-22 15:47:33", "Vyatta is disrupting the networking industry by delivering a software-based network operating system that is portable to standard x86 hardware as well as common virtualization and cloud computing platforms. Vyatta software provides a complete enterprise-class routing and security suite capable of uniquely addressing the next-generation infrastructure requirements of flexibility, on-demand delivery and platform independence. Thousands of physical and virtual infrastructures around the world, from small enterprise to Fortune 500 customers, are connected and protected by Vyatta.", "Vyatta", "", "publish", "closed", "closed", "", "vyatta", "", "", "2015-07-22 15:47:33", "2015-07-22 15:47:33", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=149", "0", "board_members", "", "0");
