INSERT INTO `wp_posts` VALUES("153", "1", "2015-07-22 15:48:53", "2015-07-22 15:48:53", "FRAFOS offers SIP session management and security solutions of the latest generation that come either as a standalone solution or as a cloud ready implementation. The flagship product of FRAFOS, the ABC SBC, offers open interfaces and built in multimedia applications such as recording and announcements. The ABC SBC enables the operators to simplify their service infrastructure and prepares them for future challenges.", "FRAFOS", "", "publish", "closed", "closed", "", "frafos", "", "", "2015-07-22 15:48:53", "2015-07-22 15:48:53", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=153", "0", "board_members", "", "0");
