INSERT INTO `wp_posts` VALUES("155", "1", "2015-07-22 15:49:25", "2015-07-22 15:49:25", "Metaswitch Networks is powering the transition of communication networks into a cloud-based, software-centric future. With a reputation earned by solving tough technical problems, Metaswitch develops openly programmable solutions that run on standard hardware or in virtualized environments and act as the key control points in elastic voice, video and data networks.", "Metaswitch Networks", "", "publish", "closed", "closed", "", "metaswitch-networks", "", "", "2015-07-22 15:49:25", "2015-07-22 15:49:25", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=155", "0", "board_members", "", "0");
