INSERT INTO `wp_posts` VALUES("157", "1", "2015-07-22 15:50:02", "2015-07-22 15:50:02", "PeerApp enables operators to embrace the exploding demand for over-the-top (OTT) video and data content. PeerApp’s award-winning multiservice UltraBand transparent caching platform enhances subscribers’ quality of experience (QoE), improves network efficiency, and provides a platform for monetizing OTT content delivery. It does this by intelligently caching and bringing the most frequently requested Internet content closer to the subscriber.", "Peer App", "", "publish", "closed", "closed", "", "peer-app", "", "", "2015-07-22 15:50:02", "2015-07-22 15:50:02", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=157", "0", "board_members", "", "0");
