INSERT INTO `wp_posts` VALUES("159", "1", "2015-07-22 15:50:36", "2015-07-22 15:50:36", "IPgallery delivers converged Voice, Data, Messaging and Video communication solutions that utilize HTML-5 Thin-Client and offer innovative communication services on general purpose machines in the Cloud / NFV. IPgallery’s solutions enable Fixed and Mobile Communication Service Providers (CSP) to rapidly rollout advanced apps and services for Private and Enterprise consumers. IPgallery’s services and apps provide a personalized unified social communication experience on any device, anywhere, anytime.", "IPgallery", "", "publish", "closed", "closed", "", "ipgallery", "", "", "2015-07-22 15:50:36", "2015-07-22 15:50:36", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=159", "0", "board_members", "", "0");
