INSERT INTO `wp_posts` VALUES("163", "1", "2015-07-22 15:54:14", "2015-07-22 15:54:14", "Procera Networks delivers Internet Intelligence solutions designed for carriers, service providers and enterprises worldwide. Procera’s PacketLogic solutions provide actionable intelligence and policy enforcement to ensure a high quality experience for any Internet connected devices. Network operators deploy Procera’s technology to enable real-time visibility, superior performance and scalability, and deliver personalized services for millions of enterprises and consumers. Enterprises utilize Procera’s embedded NAVL solutions to ensure that they can deliver on BYOD and Cloud services to their employees.", "Procera", "", "publish", "closed", "closed", "", "procera", "", "", "2015-07-22 15:54:14", "2015-07-22 15:54:14", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=163", "0", "board_members", "", "0");
