INSERT INTO `wp_posts` VALUES("165", "1", "2015-07-22 15:54:53", "2015-07-22 15:54:53", "ConteXtream is a privately held software-defined-networking (SDN) company that enables carriers to deliver network capacity and functions in the same way cloud providers deliver applications effectively utilizing standard compute and storage. ConteXtream’s SDN offering is carrier-grade and enables Network Function Virtualization (NFV) for various solutions deployed on carrier network (link to solutions page). Deployed by Tier-1 operators, ConteXtream’s Carrier-SDN dynamically and elastically connects subscribers to services and enables carriers to leverage standard, low-cost server hardware and hypervisors to virtualize functions and services, while replacing costly purpose-built proprietary systems.", "ConteXtream", "", "publish", "closed", "closed", "", "contextream", "", "", "2015-07-22 15:54:53", "2015-07-22 15:54:53", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=165", "0", "board_members", "", "0");
