INSERT INTO `wp_posts` VALUES("167", "1", "2015-07-22 15:55:32", "2015-07-22 15:55:32", "PT Inovação e Sistemas is a technological company of the Portugal Telecom Group focused on the development of innovative products and services, as well as on the integration of systems in the Telco and IT markets. PT Inovação e Sistemas’ mission is to provide transformation and technological innovation to customers, allowing them to create more advanced service offers.\r\nPT InS pursues innovation – at the services, technologies and operations level – by developing several skills in the Telco and ICT market sectors.", "PT Inovação e Sistemas", "", "publish", "closed", "closed", "", "pt-inovacao-e-sistemas", "", "", "2015-07-22 15:55:32", "2015-07-22 15:55:32", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=167", "0", "board_members", "", "0");
