INSERT INTO `wp_posts` VALUES("169", "1", "2015-07-22 15:56:17", "2015-07-22 15:56:17", "Qosmos turns networks into information sources, making them application- and service-aware. The company’s award-winning Deep Packet Inspection (DPI) and Layer 7 Network Intelligence (NI) technologies recognize thousands of communication protocols and metadata attributes, creating an essential information layer that gives the most accurate picture of real-time data activity on networks.\r\nA pioneer in embedded Network Intelligence software, Qosmos is the leading independent OEM provider on the market today, serving more than 60 companies worldwide. Based in Paris, Qosmos has offices in the Silicon Valley, Singapore and London.", "Qosmos", "", "publish", "closed", "closed", "", "qosmos", "", "", "2015-07-22 15:56:17", "2015-07-22 15:56:17", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=169", "0", "board_members", "", "0");
