INSERT INTO `wp_posts` VALUES("171", "1", "2015-07-22 15:56:53", "2015-07-22 15:56:53", "RADCOM provides innovative service assurance and customer experience management solutions for leading telecom operators and communications service providers. RADCOM specializes in solutions for next-generation mobile and fixed networks, including LTE, LTE-A, VoLTE, IMS, VoIP, UMTS/GSM and mobile broadband. RADCOM’s comprehensive, carrier- grade solutions are designed for big data analytics on terabit networks, and are used to prevent service provider revenue leakage and to enhance customer care management. RADCOM’s products interact with policy management to provide self-optimizing network solutions.", "RADCOM", "", "publish", "closed", "closed", "", "radcom", "", "", "2015-07-22 15:56:53", "2015-07-22 15:56:53", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=171", "0", "board_members", "", "0");
