INSERT INTO `wp_posts` VALUES("173", "1", "2015-07-22 15:59:13", "2015-07-22 15:59:13", "EXFO is a leading provider of next-generation test and service assurance solutions for wireline and wireless network operators and equipment manufacturers in the global telecommunications industry. It offers solutions for the development, installation, management and maintenance of converged, IP fixed and mobile networks. Key technologies supported include 3G, 4G/LTE, IMS, Ethernet, OTN, FTTx, xDSL. With 1600 employees in 25 countries, it supports over 2000 customers worldwide, and is listed on the NASDAQ and TSX stock exchanges.", "EXFO", "", "publish", "closed", "closed", "", "exfo", "", "", "2015-07-22 15:59:13", "2015-07-22 15:59:13", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=173", "0", "board_members", "", "0");
