INSERT INTO `wp_posts` VALUES("175", "1", "2015-07-22 15:59:53", "2015-07-22 15:59:53", "Cellwize provides cutting-edge SON solutions to mobile operators. Our proprietary elastic-SON™ continuously reacts to real-time changes and matches capacity, coverage and quality with evolving usage patterns and users’ needs. It uses Big Data insight to transform spotty and volatile networks that struggle to meet demand into user-centric, value-driven and over-performing mobile networks across multiple vendors and wireless technologies (Multi-RAT). Our proven technology improves network management and reduces on-going CAPEX and OPEX investments.", "Cellwize", "", "publish", "closed", "closed", "", "cellwize", "", "", "2015-07-22 15:59:53", "2015-07-22 15:59:53", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=175", "0", "board_members", "", "0");
