INSERT INTO `wp_posts` VALUES("177", "1", "2015-07-22 16:01:09", "2015-07-22 16:01:09", "Netrounds is a SaaS solution for instant troubleshooting and verification. It simplifies quality assurance and helps finding and fixing IP network performance issues in minutes instead of weeks. Netrounds leverages public cloud infrastructure for central control and storage, combined with downloadable x86 appliances for bare metal or NFV deployments. Netrounds combines monitoring and on-demand testing using built-in capabilities for IPTV and SIP, IPv6 synthetic traffic generation, remote packet capturing and Metro Ethernet Forum service activation tests.", "Netrounds", "", "publish", "closed", "closed", "", "netrounds", "", "", "2015-07-22 16:01:09", "2015-07-22 16:01:09", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=177", "0", "board_members", "", "0");
