INSERT INTO `wp_posts` VALUES("179", "1", "2015-07-22 16:02:43", "2015-07-22 16:02:43", "Sonus Networks is a leader in securing real-time communications. With service provider and Fortune 500 customers in more than 50 countries across the globe, Sonus offers a complete portfolio of hardware-based and virtualized Session Border Controllers (SBCs) as well as Diameter and SS7 Signaling Systems that deliver advanced routing/applications to SS7, 4G/LTE and IMS networks.", "Sonus", "", "publish", "closed", "closed", "", "sonus", "", "", "2015-07-22 16:02:43", "2015-07-22 16:02:43", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=179", "0", "board_members", "", "0");
