INSERT INTO `wp_posts` VALUES("183", "1", "2015-07-22 16:04:09", "2015-07-22 16:04:09", "Sandvine’s network policy control solutions add intelligence to fixed, mobile and converged service provider networks to enable services that increase revenue and reduce network costs. Sandvine’s equipment performs end-to-end policy control functions, including traffic classification, and policy decision and enforcement across the data, control and business planes. Sandvine’s products provide actionable business insight, the ability to deploy new services and tools to optimize traffic while enhancing subscriber quality of experience.", "Sandvine", "", "publish", "closed", "closed", "", "sandvine", "", "", "2015-07-22 16:04:09", "2015-07-22 16:04:09", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=183", "0", "board_members", "", "0");
