INSERT INTO `wp_posts` VALUES("187", "1", "2015-07-22 16:05:26", "2015-07-22 16:05:26", "Ixia solutions optimize networks and data centers to accelerate, secure, and scale the delivery of applications and services. Ixia delivers the industry’s only solution with complete visibility into critical network, data center, applications, and services. Ixia provides actionable insight for service providers with solutions that test, assess, and optimize networks. Ixia solutions uses predictive data to accelerate and secure application and service delivery, giving customers the confidence to know their business networks are engineered to be understandable, dynamic, and resilient.", "Ixia", "", "publish", "closed", "closed", "", "ixia", "", "", "2015-07-22 16:05:26", "2015-07-22 16:05:26", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=187", "0", "board_members", "", "0");
