INSERT INTO `wp_posts` VALUES("201", "1", "2015-07-22 16:17:34", "2015-07-22 16:17:34", "Phonedeck is a Berlin-based tech startup that develops solutions combining mobile communications and IT systems. Based on the Salesforce1 Platform, the products are supporting sales and service reps in their daily work and are boosting their productivity. Phonedeck’s mission is to connect the world of CRM, IT and telecommunication networks and to create a new user experience for mobile working. Alcatel-Lucent awarded Phonedeck the Innovative Leader in IMS award during its Munich Hackathon in June 2014.", "Phonedeck", "", "publish", "closed", "closed", "", "phonedeck", "", "", "2015-07-22 16:17:34", "2015-07-22 16:17:34", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=201", "0", "board_members", "", "0");
