INSERT INTO `wp_posts` VALUES("246", "1", "2015-07-22 16:43:43", "2015-07-22 16:43:43", "6WIND solves performance challenges for network vendors in telecom, enterprise and cloud infrastructure markets. Its 6WINDGate™ packet processing software is optimized for cost-effective hardware running Linux with a choice of multicore processors to deliver a wide variety of networking and security protocols and features. By solving critical data plane performance challenges on multicore architectures, 6WINDGate enables a cost-effective value proposition, enabling the transition to software-defined networks (SDN) and network functions virtualization (NFV).", "6WIND", "", "publish", "closed", "closed", "", "6wind", "", "", "2015-07-22 16:43:43", "2015-07-22 16:43:43", "", "0", "http://ecosystem.cloud-band.com/?post_type=board_members&#038;p=246", "0", "board_members", "", "0");
