INSERT INTO `wp_posts` VALUES("563", "1", "2015-08-14 21:16:35", "2015-08-14 16:16:35", "<div class=\"row\">\r\n<div class=\"col-md-4\"><iframe src=\"//www.youtube.com/embed/EOxF4Med2PE?rel=0\" width=\"288\" height=\"160\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe>\r\n<h3 style=\"text-align: center;\"><strong>Demystifying the Mano by Ron Haberman, GM of CloudBand Business Unit</strong></h3>\r\n<p style=\"text-align: center;\">Ron Haberman, General Manager of the CloudBand Business Unit, discusses “Demystifying the Mano” at the Carrier Network Virtualization Event in Palo Alto from December 9 – 11, 2014.</p>\r\n\r\n</div>\r\n<div class=\"col-md-4\"><iframe src=\"//www.youtube.com/embed/f1WfXJbKZ6c?rel=0\" width=\"288\" height=\"160\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe>\r\n<h3 style=\"text-align: center;\"><strong>vEPC by Guy Machlev, Cloud Product Director</strong></h3>\r\n<p style=\"text-align: center;\">Guy Machlev, Cloud Product Director at Alcatel-Lucent discusses the work he has done around vEPC at the Carrier Network Virtualization Event in Palo Alto from December 9 – 11, 2014.</p>\r\n\r\n</div>\r\n<div class=\"col-md-4 last\"><iframe src=\"http://gartner.mediasite.com/Mediasite/Play/81ccd25c206841cb80408415eb42f6761d\" width=\"288\" height=\"160\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe>\r\n<h3 style=\"text-align: center;\"><strong>OpenStack transforms telecommunications: lessons learned from Red Hat and Alcatel-Lucent.</strong></h3>\r\n<p style=\"text-align: center;\">To stay ahead of their competition, telecom providers need to constantly create better, more innovative products and get them faster to market. Idan Mor will present Alcatel-Lucent’s CloudBand NFV Platform and Alessandro Perilli will discuss how you can accelerate innovation and get your new technologies into the market faster.</p>\r\n\r\n</div>\r\n</div><!--row-->\r\n\r\n<div class=\"clearboth\"></div>\r\n<div class=\"row\">\r\n<div class=\"col-md-4\"><iframe src=\"//www.youtube.com/embed/_dDa_KJ6874?rel=0\" width=\"288\" height=\"160\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe>\r\n<h3 style=\"text-align: center;\"><strong>CloudBand, Red Hat and Intel: Leading the Journey to Power NFV Operations</strong></h3>\r\n<p style=\"text-align: center;\">Openness and collaboration is key to making NFV a reality. Alcatel-Lucent is working with two great companies Red Hat and Intel, to make NFV deployable at scale. Listen to Dor Skuler and representatives from Red Hat and Intel discuss this joint collaboration.</p>\r\n\r\n</div>\r\n<div class=\"col-md-4\"><iframe src=\"//www.youtube.com/embed/LIwnXmCwH30?rel=0\" width=\"288\" height=\"160\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe>\r\n<h3 style=\"text-align: center;\"><strong>Red Hat and CloudBand</strong></h3>\r\n<p style=\"text-align: center;\">The right partnership to support a Service Providers journey to the Cloud.</p>\r\n\r\n</div>\r\n<div class=\"col-md-4 last\">\r\n\r\n<iframe src=\"//www.youtube.com/embed/xTK9BcEGhmY?rel=0\" width=\"288\" height=\"160\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe>\r\n<h3 style=\"text-align: center;\"><strong>Happy Birthday ETSI NFV</strong></h3>\r\n<p style=\"text-align: center;\">Alcatel-Lucent and Telefonica were the sponsors of ETSI NFV#5 that took place in Malaga, Spain in February 2014.</p>\r\n\r\n</div>\r\n</div><!--last-->\r\n<div class=\"clearboth\"></div>\r\n<div class=\"row\">\r\n<div class=\"col-md-4\">\r\n\r\n<script src=\"http://vdassets.bitgravity.com/api/script\" type=\"text/javascript\"></script><script type=\"text/javascript\">// <![CDATA[\r\nviewNode(\"cafe0a6dd1f4e\", {\"server_detection\": true, \"width\": 288, \"height\": 160, \"player_profile\": \"Flow-player-no-play\"});\r\n// ]]&gt;\r\n// ]]></script>\r\n<h3 style=\"text-align: center;\"><strong>Mobile World Congress, 2014</strong></h3>\r\n<p style=\"text-align: center;\">Dor Skuler (Alcatel-Lucent) and Sandra Rivera (Intel) discuss moving the NFV market forward. Mobile World Congress, 2014</p>\r\n\r\n</div>\r\n<div class=\"col-md-4\">\r\n</div>\r\n<div class=\"col-md-4\">\r\n</div>\r\n</div><!--row-->", "Videos", "", "inherit", "open", "open", "", "516-revision-v1", "", "", "2015-08-14 21:16:35", "2015-08-14 16:16:35", "", "516", "http://ecosystem.cloud-band.com/516-revision-v1/", "0", "revision", "", "0");
