INSERT INTO `wp_posts` VALUES("849", "5", "2015-10-13 19:00:11", "2015-10-13 14:00:11", "Nexus Telecom is a global provider of telecom network management software solutions. Our solution portfolio improves operational efficiency and customer satisfaction through monitoring and testing network services. As a reliable partner, Nexus Telecom solutions help grow business revenues for fixed, mobile and international operators, covering technologies including NGN, LTE, VoLTE/IMS, VoIP, and those operating in Network Function Virtualized (NFV) environments. nexustelecom.com\r\n \r\n\r\n", "Nexus Telecom", "", "inherit", "open", "open", "", "839-autosave-v1", "", "", "2015-10-13 19:00:11", "2015-10-13 14:00:11", "", "839", "http://ecosystem.cloud-band.com/839-autosave-v1/", "0", "revision", "", "0");
